import './banner.css'

// Page à propos > Contient une bannière et renseignements sur Kasa
function Banner({image,texte}) {
    return (
        <div className='bannerContainer'>
            <img className='bannerImg' src={image} alt='Paysage' />
            <p>{texte}</p>
        </div>
   )
 }
 
 export default Banner
