// IMPORTS
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './location.scss';

// COMPONENTS
import Error from '../Error/error';
import Slider from '../../components/Slider/slider';
import Rating from '../../components/Rating/Rating';
import Collapse from '../../components/Collapse/Collapse'

// ASSETS
import DataLocation from '../../datas/locations.json';



// Page Logement > Contient le Composant Slider, composant Rating et le composant Collapse description ainsi que les infos du logement et du propriétaire
function Logement() {
   const [logement, setLogement] = useState(null); 
   const { id } = useParams(); // Récupère l'id du logement dans l'url

   useEffect(() => {  // Récupère les infos du logement en fonction de l'id
      const data = DataLocation.find(location => location.id === id);
      if (data) {
      setLogement(data);
   } else { 
      setLogement(null);
   }
   }, [id]);

   if (!logement) { // Si l'id n'existe pas, affiche la page d'erreur
      return (<Error />);
   }

   return ( 
      <>
         {logement.pictures && <Slider images={logement.pictures} />}

         <section className='logementContainer'>

            <div className='Infos'>
               <h1 className='Title'>{logement.title}</h1>
               <p className='Text'>{logement.location}</p>
               <ul className='TagUl'>
                  {logement.tags.map(tag => (
                     <li className='TagLi' key={tag}>{tag}</li>
                  ))}
               </ul>
            </div>

            <div className='Owner'>
               <div className='Description'>
                  <p className='Name'>{logement.host.name}</p>
                  <img className='Img' src={logement.host.picture} alt={logement.host.name} />
               </div>
               <Rating rating={logement.rating} />
            </div>

         </section>

         <div className="collapsesContainer">
					<div className="logementCollapseContainer">
						<Collapse title={'Description'} content={logement.description}/>
					</div>
					<div className="logementCollapseContainer">
						<Collapse title={'Équipements'} content={logement.equipments}/>
					</div>
			</div>

      </>
  );
}


export default Logement;
