// IMPORTS
import { useState } from 'react'
import './collapse.scss'

// ASSETS
import ArrowDown from '../../assets/arrow-down.png'
import ArrowUp from '../../assets/arrow-up.png'


function Collapse({ data }) {
  const {isOpen, setIsOpen} = useState(false);

  const display = () => {
    setIsOpen(!isOpen);
  };

  return (
    // affiche le collapse replié par défaut et l'ouvre au clic puis le referme au clic en faisant disparaitre le texte et le style
    <div className="collapseContainer">
      <div className="collapseContainerTitle">
        <h3 className="collapseTitle">{Collapse.title}</h3>
        <p onClick={display}>
          {isOpen ? (
            <img class="collapseArrow" src={ArrowUp} alt="Arrow" />
          ) : (
            <img className="collapseArrow" src={ArrowUp} alt="Arrow" />
          )}
        </p>
      </div>
      {/* Si le collapse est à TRUE alors il affichera la description */}
      <div className="collapseText">
        {isOpen && <p>{Collapse.text}</p>}
      </div>
    </div>
  )
}

export default Collapse
