import { useState } from 'react'
import './collapse.scss'
import ArrowUp from '../../assets/arrow-up.png'


function Collapse({ data }) {
  const [active, setActive] = useState(false)

  const displayContent = (e) => {
    console.log(e)
    console.log(Collapse.title)
    setActive(!active)
  }

  return (
    // affiche le collapse replié par défaut et l'ouvre au clic puis le referme au clic en faisant disparaitre le texte et le style
    <div className="collapseContainer">
      <div className="collapseContainerTitle" onClick={displayContent}>
        <h3 className="collapseTitle">{Collapse.title}</h3>
        <img class={`collapseArrow ${active && "active"}`} src={ArrowUp} alt="Arrow" />
      </div>
      {/* Si le collapse est à TRUE alors il affichera la description */}
      <p className={`collapseText ${active && "active"}`}>{Collapse.text}</p>
    </div>
  )
}

export default collapse
