import './logement.scss'
import Card from './Card/Card'
import Full from './Full/Full'


// Composant CARD > Génère les cartes de présentations des locations dans la page home
// les éléments sont directement lus dans le tableau LOCATIONS.JSON

function Logement({modeDisplay,logement}) {
   console.log(logement.id, modeDisplay)
   if (modeDisplay === "card") {
      return (
         <Card location={logement} />
      )
   } else {
      return (
         <Full location={logement} />
      )
   }
}

export default Logement
