// IMPORTS
import './banner.css'

// Page à propos > Contient une bannière et renseignements sur Kasa
function Banner(props) {
    const ImgBanner = props.picture
    const textBanner = props.textBanner
    return (
        <div className='bannerContainer'>
            <img className='bannerImg' src={ImgBanner} alt='Paysage' />
            <p>{textBanner}</p>
        </div>
   )
 }
 
 export default Banner
