// IMPORTS
import { Link } from 'react-router-dom'
import './card.css'

// ASSETS
import DataLocation from '../../datas/locations.json'


// Composant CardLocation > Génère des cards avec les images des locations, dynamiquement depuis le fichier JSON
function cardLocation() {
   return (
      <section className='cardsContainer'>
         {DataLocation.map((location) => (

            <Link to={`/location/${location.id}`} key={location.id} >
               <figure className='card'>
                  <img className='imgCard' src={location.cover} alt={location.title}/>
                  <div className='gradientCard'></div>          
                  <figcaption className='titleCard'>{location.title}</figcaption>
               </figure>
            </Link>

         ))}
      </section>
   )
}
export default cardLocation
